<?php $zones=get_option('tmw_am_banner_zones',[]);?><div class='wrap'><h1>Banner Zones</h1><form method='post'><?php wp_nonce_field('tmw_am_nonce','tmw_am_nonce');?><input type='hidden' name='tmw_am_action' value='save_zones'/><table class='form-table'><tbody><?php foreach(['desktop_global','mobile_global'] as $key):$label=$zones[$key]['label']??ucfirst(str_replace('_',' ',$key));$html=$zones[$key]['html']??'';?><tr><th><label><?php echo esc_html($label);?></label></th><td><textarea name='zones[<?php echo esc_attr($key);?>][html]' rows='8' class='large-text code'><?php echo esc_textarea($html);?></textarea><input type='hidden' name='zones[<?php echo esc_attr($key);?>][label]' value='<?php echo esc_attr($label);?>'/></td></tr><?php endforeach;?></tbody></table><?php submit_button('Save Zones');?></form></div>